package apcs.classes;

public class IntegerRunner {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer i1;
		i1 = new Integer (10);
		int x;
		x = i1.intValue();
		System.out.println("i1 =" + x);
		
		Integer i2;
		i2 = new Integer (10);

		String s;
		s = i2.toString();
		System.out.println ("i2 = " + s);
		
		Integer i3;
		i3 = new Integer (20);


		System.out.println ("i3 = " + i3.toString());

	}

}
